import React, { useState } from 'react';
import './App.css';
import BoardComponent from './components/BoardComponent';


const App = () => {
  const [board, setBoard] = useState(new Board())

  useEffect( () => {
    restart()
  }, [])

  function restart(){
    const newBoard = new Board();
    newBoard.initCells()
    setBoard(newBoard)
  }

  return (
      <div className="App">
        <BoardComponent
          board={board}
          setBoard={setBoard}
        />
      </div>
  );
};

export default App;
